SELECT DISTINCT ldsLabel.en AS label FROM studyGroup sg JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id JOIN study s ON s.studyGroup_id=sg.id JOIN resource r_s ON r_s.id=s.id JOIN langstring ls_s ON ls_s.id=r_s.prefLabel_id JOIN study_logicalDataSet s_lds ON s_lds.study_id=s.id JOIN resource lds_r ON lds_r.id=s_lds.logicalDataSet_id JOIN langString ldsLabel ON ldsLabel.id=lds_r.prefLabel_id WHERE ls_sg.en = 'EU-SILC' AND ls_s.en = '2007' AND ldsLabel.en LIKE 'cross-sec/%';