SELECT v.id, frequency_sum.frequency, SUM( ss.numberOfCases ) FROM summaryStatistics ss JOIN summaryStatistics_variable ss_v ON ss_v.summaryStatistics_id=ss.id JOIN variable v ON v.id=ss_v.variable_id JOIN ( SELECT SUM( cst.frequency ) AS frequency, cs_c.conceptScheme_id AS conceptScheme_id FROM categoryStatistics cst JOIN categoryStatistics_concept cst_c ON cst_c.categoryStatistics_id=cst.id JOIN conceptScheme_concept cs_c ON cs_c.hasTopConcept_id=cst_c.concept_id GROUP BY cs_c.conceptScheme_id ) frequency_sum ON frequency_sum.conceptScheme_id = v.representation_id GROUP BY v.id