SELECT lssg.en, lss.en FROM study s JOIN studyGroup sg on sg.id=s.studyGroup_id JOIN langstring lss ON lss.id=s.title_id JOIN langstring lssg ON lssg.id=sg.title_id JOIN study_publisher sp ON sp.study_id=s.id JOIN agent a ON a.id=sp.agent_id JOIN resource ra ON ra.id=a.id JOIN langstring ls ON ls.id=ra.prefLabel_id JOIN study_periodoftime spt ON spt.Study_id=s.id JOIN periodoftime p ON p.id=spt.temporal_id WHERE ls.en LIKE 'Eurostat%' AND p.startDate>='2000' AND p.startDate<='2007' ORDER BY lssg.en, lss.en;