SELECT DISTINCT BINARY qt.en FROM variable variable JOIN variable_question v_q ON v_q.variable_id=variable.id JOIN question q ON q.id=v_q.question_id JOIN langstring qt ON qt.id=q.questionText_id JOIN concept cv ON cv.id=variable.id JOIN concept variable_concept ON variable_concept.id=variable.concept_id JOIN concept_conceptbroader level1 ON level1.concept_id=variable_concept.id JOIN concept parent1 ON parent1.id=level1.broader_id LEFT JOIN concept_conceptbroader level2 ON level2.concept_id=parent1.id LEFT JOIN concept parent2 ON parent2.id=level2.broader_id LEFT JOIN concept_conceptbroader level3 ON level3.concept_id=parent2.id LEFT JOIN concept parent3 ON parent3.id=level3.broader_id WHERE variable_concept.notation LIKE '%age%' AND ( Parent1.notation LIKE '%Income%' OR parent2.notation LIKE '%Income%' OR parent3.notation LIKE '%Income%' );