SELECT DISTINCT ls_i.en FROM study s JOIN instrument I ON I.study_id=s.id JOIN questionnaire q ON q.id=i.id JOIN langstring ls_i ON ls_i.id=i.description_id JOIN country co ON co.id=q.country_id JOIN resource r_s ON r_s.id=s.id JOIN langString ls_s ON ls_s.id=r_s.prefLabel_id JOIN studyGroup sg ON sg.id=s.studyGroup_id JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id WHERE ls_sg.en = 'EU-SILC' AND ls_s.en = '2005' AND co.code = 'UK';