SELECT DISTINCT q.* FROM study s JOIN instrument I ON I.study_id=s.id JOIN langString ls_i ON ls_i.id=i.description_id JOIN questionnaire_question q_q ON q_q.questionnaire_id=i.id JOIN questionnaire qn ON qn.id=q_q.questionnaire_id JOIN country co ON co.id=qn.country_id JOIN question q ON q.id=q_q.question_id JOIN langString qt ON qt.id=q.questionText_id JOIN resource r_s ON r_s.id=s.id JOIN langString ls_s ON ls_s.id=r_s.prefLabel_id JOIN studyGroup sg ON sg.id=s.studyGroup_id JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id WHERE ls_sg.en = 'EU-SILC' AND ls_s.en = '2005' AND ls_i.en = 'basic questionnaire' AND co.code = 'UK';