SELECT DISTINCT ls_s.en FROM variable v JOIN concept c_v ON c_v.id=v.id JOIN logicalDataSet_variable lds_v ON lds_v.variable_id=v.id JOIN study_logicalDataSet s_lds ON s_lds.logicalDataSet_id=lds_v.logicalDataSet_id JOIN study s ON s.id=s_lds.study_id JOIN resource r_s ON r_s.id=s.id JOIN langString ls_s ON ls_s.id=r_s.prefLabel_id JOIN studyGroup sg ON sg.id=s.studyGroup_id JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id WHERE ls_sg.en = 'EU-SILC' AND c_v.notation = 'PL080';