SELECT DISTINCT ls_i.en, qt.en FROM study s JOIN instrument I ON I.study_id=s.id JOIN langstring ls_i ON ls_i.id=i.description_id JOIN questionnaire_question q_q ON q_q.questionnaire_id=i.id JOIN questionnaire qu ON qu.id=i.id JOIN country co ON co.id=qu.country_id JOIN question q ON q.id=q_q.question_id JOIN langString qt ON qt.id=q.questionText_id JOIN study_logicalDataSet s_lds ON s_lds.study_id=s.id JOIN logicalDataSet lds ON lds.id=s_lds.logicalDataSet_id JOIN resource lds_r ON lds_r.id=lds.id JOIN langString ls_lds ON ls_lds.id=lds_r.prefLabel_id JOIN resource r_s ON r_s.id=s.id JOIN langString ls_s ON ls_s.id=r_s.prefLabel_id JOIN studyGroup sg ON sg.id=s.studyGroup_id JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id WHERE ls_sg.en = 'EU-LFS' AND ls_s.en = '2008' AND ls_lds.en = 'yearly' AND co.code = 'UK';