SELECT ss.* FROM study s JOIN resource r_s ON r_s.id=s.id JOIN langString ls_s ON ls_s.id=r_s.prefLabel_id JOIN studyGroup sg ON sg.id=s.studyGroup_id JOIN resource r_sg ON r_sg.id=sg.id JOIN langString ls_sg ON ls_sg.id=r_sg.prefLabel_id JOIN study_logicalDataSet s_lds ON s_lds.study_id=s.id JOIN logicalDataSet_variable lds_v ON lds_v.logicalDataSet_id=s_lds.logicalDataSet_id JOIN logicalDataSet lds ON lds.id=lds_v.logicalDataSet_id JOIN resource lds_r ON lds_r.id=lds.id JOIN langString ls_lds ON ls_lds.id=lds_r.prefLabel_id JOIN concept c_v ON c_v.id=lds_v.variable_id JOIN summaryStatistics_variable ss_v ON ss_v.variable_id=lds_v.variable_id JOIN summaryStatistics_location ss_l ON ss_l.summaryStatistics_id=ss_v.summaryStatistics_id JOIN location lo ON lo.id=ss_l.location_id JOIN summaryStatistics ss ON ss.id=ss_l.summaryStatistics_id WHERE ls_sg.en = 'EU-LFS' AND ls_s.en = '1996' AND ls_lds.en = 'yearly' AND c_v.notation = 'STAPRO' AND lo.label = 'PT';